import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ManageStations = () => {
  const [stations, setStations] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    latitude: "",
    longitude: "",
    slots: "",
  });

  const navigate = useNavigate();

  const styles = {
    container: {
      backgroundImage: "url('../image/evbackground8.webp')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      minHeight: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      padding: "20px",
    },
    contentContainer: {
      background: "rgba(255, 255, 255, 0.85)", // Semi-transparent white
      padding: "30px",
      borderRadius: "10px",
      width: "80%",
      maxWidth: "800px",
      boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
      textAlign: "center",
    },
    heading: {
      fontSize: "24px",
      fontWeight: "bold",
      marginBottom: "20px",
    },
    form: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      gap: "10px",
      marginBottom: "20px",
    },
    input: {
      width: "300px",
      padding: "10px",
      fontSize: "16px",
      border: "1px solid #ccc",
      borderRadius: "5px",
    },
    addButton: {
      backgroundColor: "blue",
      color: "white",
      padding: "10px",
      fontSize: "16px",
      border: "none",
      cursor: "pointer",
      borderRadius: "5px",
      width: "50%",
    },
    table: {
      width: "100%",
      borderCollapse: "collapse",
      marginTop: "20px",
      background: "white",
      borderRadius: "5px",
      overflow: "hidden",
    },
    thTd: {
      border: "1px solid #ddd",
      padding: "10px",
      textAlign: "center",
    },
    buttonContainer: {
      display: "flex",
      justifyContent: "center",
      gap: "10px",
    },
    updateButton: {
      backgroundColor: "green",
      color: "white",
      padding: "5px 10px",
      border: "none",
      cursor: "pointer",
      borderRadius: "5px",
    },
    deleteButton: {
      backgroundColor: "red",
      color: "white",
      padding: "5px 10px",
      border: "none",
      cursor: "pointer",
      borderRadius: "5px",
    },
  };

  useEffect(() => {
    fetchStations();
  }, []);

  // Fetch stations from backend
  const fetchStations = async () => {
    try {
      const response = await fetch("http://localhost:5006/api/stations");
      const data = await response.json();
      setStations(data);
    } catch (error) {
      console.error("Error fetching stations:", error);
    }
  };

  // Handle form input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add a new station
  const handleAddStation = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5006/api/stations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        fetchStations(); // Refresh list after adding
        setFormData({ name: "", latitude: "", longitude: "", slots: "" });
      }
    } catch (error) {
      console.error("Error adding station:", error);
    }
  };

  // Delete a station
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5006/api/stations/${id}`, { method: "DELETE" });
      fetchStations(); // Refresh list after deleting
    } catch (error) {
      console.error("Error deleting station:", error);
    }
  };

  // Navigate to update page
  const handleUpdate = (id) => {
    navigate(`/update-station/${id}`);
  };

  return (
    <div style={styles.container}>
      <div style={styles.contentContainer}>
        <h2 style={styles.heading}>MANAGE CHARGING STATIONS</h2>

        {/* Add Station Form */}
        <form onSubmit={handleAddStation} style={styles.form}>
          <input type="text" name="name" placeholder="Station Name" value={formData.name} onChange={handleChange} required style={styles.input} />
          <input type="number" name="latitude" placeholder="Latitude" value={formData.latitude} onChange={handleChange} required style={styles.input} />
          <input type="number" name="longitude" placeholder="Longitude" value={formData.longitude} onChange={handleChange} required style={styles.input} />
          <input type="number" name="slots" placeholder="Total Slots" value={formData.slots} onChange={handleChange} required style={styles.input} />
          <button type="submit" style={styles.addButton}>Add Station</button>
        </form>

        {/* Station List */}
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.thTd}>Name</th>
              <th style={styles.thTd}>Latitude</th>
              <th style={styles.thTd}>Longitude</th>
              <th style={styles.thTd}>Slots</th>
              <th style={styles.thTd}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {stations.map((station) => (
              <tr key={station._id}>
                <td style={styles.thTd}>{station.name}</td>
                <td style={styles.thTd}>{station.latitude}</td>
                <td style={styles.thTd}>{station.longitude}</td>
                <td style={styles.thTd}>{station.slots}</td>
                <td style={styles.thTd}>
                  <div style={styles.buttonContainer}>
                    <button onClick={() => handleUpdate(station._id)} style={styles.updateButton}>Update</button>
                    <button onClick={() => handleDelete(station._id)} style={styles.deleteButton}>Delete</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageStations;
