import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";

// Google API Key
const GOOGLE_MAPS_API_KEY = "AIzaSyAS9zfp0wdczlh-d1WDk1VHAbsTrhsDg-E";

const ManageStations = () => {
  const [stations, setStations] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    latitude: "",
    longitude: "",
    slots: "",
  });

  const navigate = useNavigate();

  // ‚úÖ Get logged-in user from localStorage
  const loggedInUser = JSON.parse(localStorage.getItem("user"));

  // ‚úÖ Function to fetch stations
  const fetchStations = () => {
    if (!loggedInUser || !loggedInUser.id) {
      console.error("User is not logged in");
      return;
    }

    fetch(`/api/stations?userId=${loggedInUser.id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Stations:", data);
        setStations(data);
      })
      .catch((error) => console.error("Error fetching stations:", error));
  };

  useEffect(() => {
    fetchStations();
  }, []);

  // üìå Handle form input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // üìå Use Current Location for Latitude & Longitude
  const useCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const lat = position.coords.latitude;
          const lng = position.coords.longitude;
          setFormData({ ...formData, latitude: lat, longitude: lng });
        },
        (error) => {
          console.error("Error getting location:", error);
        }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  };

  // üìå Handle map click to update latitude & longitude
  const handleMapClick = (event) => {
    const lat = event.latLng.lat();
    const lng = event.latLng.lng();
    setFormData({ ...formData, latitude: lat, longitude: lng });
  };

  // üìå Add a new station
  const handleAddStation = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5006/api/stations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        fetchStations(); // Refresh list after adding
        setFormData({ name: "", latitude: "", longitude: "", slots: "" });
      }
    } catch (error) {
      console.error("Error adding station:", error);
    }
  };

  // üìå Delete a station
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5006/api/stations/${id}`, {
        method: "DELETE",
      });
      fetchStations();
    } catch (error) {
      console.error("Error deleting station:", error);
    }
  };

  // üìå Navigate to update page
  const handleUpdate = (id) => {
    navigate(`/update-station/${id}`);
  };

  return (
    <div>
      <h2>Manage Charging Stations</h2>

      {/* Form for Adding Stations */}
      <form onSubmit={handleAddStation}>
        <input
          type="text"
          name="name"
          placeholder="Station Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="slots"
          placeholder="Total Slots"
          value={formData.slots}
          onChange={handleChange}
          required
        />

        <button type="button" onClick={useCurrentLocation}>
          üìç Use Current Location
        </button>

        {/* Google Map */}
        <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
          <GoogleMap
            mapContainerStyle={{ height: "400px", width: "50%" }}
            center={
              formData.latitude && formData.longitude
                ? { lat: formData.latitude, lng: formData.longitude }
                : { lat: 10.0, lng: 76.0 }
            }
            zoom={10}
            onClick={handleMapClick}
          >
            {formData.latitude && formData.longitude && (
              <Marker
                position={{
                  lat: formData.latitude,
                  lng: formData.longitude,
                }}
              />
            )}
          </GoogleMap>
        </LoadScript>

        <p>
          Selected Location: {formData.latitude}, {formData.longitude}
        </p>

        <button type="submit">Add Station</button>
      </form>

      {/* Table to Display Stations */}
      <h2>Station Details</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Latitude</th>
            <th>Longitude</th>
            <th>Slots</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {stations.map((station) => (
            <tr key={station._id}>
              <td>{station.name}</td>
              <td>{station.latitude}</td>
              <td>{station.longitude}</td>
              <td>{station.slots}</td>
              <td>
                <button onClick={() => handleUpdate(station._id)}>Update</button>
                <button onClick={() => handleDelete(station._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageStations;
