import React, { useState, useEffect } from "react";
import axios from "axios";
import { useUser } from "../context/UserContext";
import "../styles/givefeedback.css";

const FeedbackPage = () => {
  const { user } = useUser(); // Get logged-in user
  const [chargingStations, setChargingStations] = useState([]);
  const [selectedStation, setSelectedStation] = useState("");
  const [feedbackText, setFeedbackText] = useState("");
  const [loading, setLoading] = useState(false);

  // Fetch charging stations for dropdown
  useEffect(() => {
    axios.get("http://localhost:5000/api/charging-stations--for-feed")
      .then((res) => setChargingStations(res.data))
      .catch((err) => console.error("Error fetching stations:", err));
  }, []);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!feedbackText.trim()) {
      alert("Feedback cannot be empty!");
      return;
    }

    setLoading(true);
    const feedbackData = {
      userId: user._id,
      to: selectedStation === "admin" ? null : chargingStations.find(st => st._id === selectedStation)?.userId,
      chargingStationId: selectedStation === "admin" ? null : selectedStation,
      feedbackText,
      feedbackForAdmin: selectedStation === "admin",
    };

    axios.post("http://localhost:5000/api/feedback", feedbackData)
      .then(() => {
        alert("Feedback submitted successfully!");
        setFeedbackText("");
        setSelectedStation("");
      })
      .catch(() => alert("Failed to submit feedback!"))
      .finally(() => setLoading(false));
  };

  return (
    <div className="feedback-container">
      <h2>Submit Feedback</h2>

      <form onSubmit={handleSubmit}>
        {/* Dropdown to Select Charging Station or Admin */}
        <label>To:</label>
        <select value={selectedStation} onChange={(e) => setSelectedStation(e.target.value)} required>
          <option value="">Select</option>
          {chargingStations.map((station) => (
            <option key={station._id} value={station._id}>
              {station.name}
            </option>
          ))}
          <option value="admin">EV Connect (Admin)</option>
        </select>

        {/* Feedback Input */}
        <label>Feedback:</label>
        <textarea
          rows="4"
          value={feedbackText}
          onChange={(e) => setFeedbackText(e.target.value)}
          required
        />

        {/* Submit Button */}
        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Feedback"}
        </button>
      </form>
    </div>
  );
};

export default FeedbackPage;
