import React, { useState, useEffect } from "react";
import "../styles/StationMap.css";

const NearbyStations = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [stations, setStations] = useState([]);
  const [nearbyStations, setNearbyStations] = useState([]);
  const RADIUS_KM = 3; // Change this to the desired search radius

  useEffect(() => {
    getUserLocation();
  }, []);

  // Get User's Current Location
  const getUserLocation = () => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          console.log(latitude);
          console.log(longi)
          setUserLocation({ latitude, longitude });
          fetchStations(latitude, longitude);
        },
        (error) => {
          console.error("Error getting location:", error);
          alert("Please enable location services.");
        }
      );
    } else {
      alert("Geolocation is not supported by your browser.");
    }
  };

  // Fetch Charging Stations from Database
  const fetchStations = async (userLat, userLng) => {
    try {
      const response = await fetch("http://localhost:5006/api/stations/view"); // Adjust API route
      const data = await response.json();
      setStations(data);
      console.log(data);

      // Filter Nearby Stations
      const filteredStations = data.filter((station) =>
        calculateDistance(userLat, userLng, station.latitude, station.longitude) <= RADIUS_KM

      );

      setNearbyStations(filteredStations);
    } catch (error) {
      console.error("Error fetching stations:", error);
    }
  };

  // Haversine Formula to Calculate Distance Between Two Coordinates
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const toRad = (angle) => (angle * Math.PI) / 180;
    const R = 6371; // Earth’s radius in km
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c; // Distance in km
  };

  return (
    <div className="nearby-stations-container">
      <h2>Nearby Charging Stations</h2>
      {userLocation ? (
        <p>
          Your Location: {userLocation.latitude.toFixed(4)}, {userLocation.longitude.toFixed(4)}
        </p>
      ) : (
        <p>Fetching location...</p>
      )}

      <ul className="stations-list">
        {nearbyStations.length > 0 ? (
          nearbyStations.map((station) => (
            <li key={station._id}>
              <strong>{station.name}</strong> - {station.distance.toFixed(2)} km away
            </li>
          ))
        ) : (
          <p>No charging stations found within {RADIUS_KM} km.</p>
        )}
      </ul>
    </div>
  );
};

export default NearbyStations;
