import React, { useEffect, useState } from "react";
import "../styles/ViewBkEVuser.css";
import { useUser } from "../context/UserContext";

const ViewBookingEVUser = () => {
  const { user } = useUser();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBookings();
  }, []);

  // Fetch bookings for the logged-in user
  const fetchBookings = async () => {
    try {
      const response = await fetch(`http://localhost:5006/api/bookings/user/${user.id}`);
      const data = await response.json();
      setBookings(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching bookings:", error);
      setLoading(false);
    }
  };

  // Convert 24-hour format to 12-hour format
  const convertTo12HourFormat = (time) => {
    if (!time) return "";
    const [hour, minute] = time.split(":").map(Number);
    const period = hour >= 12 ? "PM" : "AM";
    const formattedHour = hour % 12 || 12;
    return `${formattedHour}:${minute.toString().padStart(2, "0")} ${period}`;
  };

  // Handle Booking Cancellation
  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm("Are you sure you want to cancel this booking?")) return;

    try {
      const response = await fetch(`http://localhost:5006/api/bookings/cancel/${bookingId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
      });

      if (response.ok) {
        alert("Booking cancelled successfully!");
        fetchBookings(); // Refresh bookings list
      } else {
        alert("Failed to cancel booking.");
      }
    } catch (error) {
      console.error("Error cancelling booking:", error);
    }
  };

  // Separate bookings into active and previous
  const today = new Date().toISOString().split("T")[0];
  const previousBookings = bookings.filter((booking) => booking.bookingDate.split("T")[0] < today);
  const activeBookings = bookings.filter((booking) => booking.bookingDate.split("T")[0] === today);

  return (
    <div className="view-booking-container">
      <h2>Your Bookings</h2>
      {loading ? (
        <p>Loading...</p>
      ) : bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        <>
          {/* Active Bookings Section */}
          {activeBookings.length > 0 && (
            <>
              <h3>Today's Bookings</h3>
              <table className="booking-table">
                <thead>
                  <tr>
                    <th>Station Name</th>
                    <th>Slot Time</th>
                    <th>Booking Date</th>
                    <th>Status</th>
                    <th>Payment</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {activeBookings.map((booking) => (
                    <tr key={booking._id}>
                      <td>{booking.chargingStation.name}</td>
                      <td>{convertTo12HourFormat(booking.slot.from)} - {convertTo12HourFormat(booking.slot.to)}</td>
                      <td>{new Date(booking.bookingDate).toLocaleDateString()}</td>
                      <td>{booking.status}</td>
                      <td>{booking.paymentStatus}</td>
                      <td>
                        {booking.status !== "cancelled" && (
                          <button className="cancel-btn" onClick={() => handleCancelBooking(booking._id)}>
                            Cancel
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </>
          )}

          {/* Previous Bookings Section */}
          {previousBookings.length > 0 && (
            <>
              <h3>Previous Bookings</h3>
              <table className="booking-table">
                <thead>
                  <tr>
                    <th>Station Name</th>
                    <th>Slot Time</th>
                    <th>Booking Date</th>
                    <th>Status</th>
                    <th>Payment</th>
                  </tr>
                </thead>
                <tbody>
                  {previousBookings.map((booking) => (
                    <tr key={booking._id}>
                      <td>{booking.chargingStation.name}</td>
                      <td>{convertTo12HourFormat(booking.slot.from)} - {convertTo12HourFormat(booking.slot.to)}</td>
                      <td>{new Date(booking.bookingDate).toLocaleDateString()}</td>
                      <td>{booking.status}</td>
                      <td>{booking.paymentStatus}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default ViewBookingEVUser;
