import React, { useState, useEffect } from "react";
import axios from "axios";
import { useUser } from "../context/UserContext";
import "../styles/ProfileOpr.css";

const StationOperatorProfile = () => {
  const { user, updateUser } = useUser(); // Get logged-in user
  const userId = user?._id; // Assume user ID is stored in context

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phoneNumber: "",
    profilePicture: "",
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fetch user profile from backend
  useEffect(() => {
    if (userId) {
      axios.get(`http://localhost:5000/api/operator/${userId}`)
        .then((res) => {
          setFormData({
            name: res.data.name,
            email: res.data.email,
            phoneNumber: res.data.phoneNumber,
            profilePicture: res.data.profilePicture || "https://via.placeholder.com/150",
          });
          setLoading(false);
        })
        .catch((err) => {
          setError("Failed to load profile");
          setLoading(false);
        });
    }
  }, [userId]);

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle profile picture upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData({ ...formData, profilePicture: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  // Handle profile update
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/api/operator/${userId}`, formData)
      .then((res) => {
        updateUser(res.data);
        alert("Profile Updated Successfully!");
      })
      .catch(() => alert("Update Failed!"));
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="profile-container">
      <h2>Station Operator Profile</h2>

      {/* Profile Picture */}
      <div className="profile-picture">
        <img src={formData.profilePicture} alt="Profile" />
        <input type="file" accept="image/*" onChange={handleImageUpload} />
      </div>

      {/* Profile Form */}
      <form onSubmit={handleSubmit}>
        <label>Name</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />

        <label>Email</label>
        <input type="email" name="email" value={formData.email} disabled />

        <label>Phone Number</label>
        <input type="text" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} required />

        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default StationOperatorProfile;
