import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { FaSearchLocation } from "react-icons/fa";
import "./EVUserDashboard.css"; // Import the CSS file for styling

const userName = "Ihzaan"; // Replace with dynamic user name

const stations = [
  { id: 1, name: "Station A", lat: 10.01, lon: 76.02 },
  { id: 2, name: "Station B", lat: 10.05, lon: 76.05 },
  { id: 3, name: "Station C", lat: 10.10, lon: 76.10 },
];

const EVUserDashboard = () => {
  const [showStations, setShowStations] = useState(false);

  return (
    <div className="dashboard-container">
      {/* Header Section */}
      <header className="dashboard-header">
        <img src="/ev-logo.png" alt="EV Connect Logo" className="logo" />
        <h1>EV Connect</h1>
      </header>

      {/* Welcome Section */}
      <div className="welcome-section">
        <h2>Welcome, {userName}!</h2>
      </div>

      {/* Search Button */}
      <div className="button-container">
        <button className="search-button" onClick={() => setShowStations(true)}>
          <FaSearchLocation className="search-icon" /> Search Stations Nearby
        </button>
      </div>

      {/* Map Section */}
      <MapContainer center={[10.0, 76.0]} zoom={10} style={{ height: "400px", width: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* Show Markers only when button is clicked */}
        {showStations &&
          stations.map((station) => (
            <Marker key={station.id} position={[station.lat, station.lon]}>
              <Popup>
                <strong>{station.name}</strong> <br />
                Location: {station.lat}, {station.lon}
              </Popup>
            </Marker>
          ))}
      </MapContainer>
    </div>
  );
};

export default EVUserDashboard;
