import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import { FaSearchLocation } from "react-icons/fa";
import "leaflet/dist/leaflet.css";
import "./stEVUserDashboard.css"; // Add CSS for styling

const EVUserDashboard = () => {
  const [userName, setUserName] = useState("");

  // Simulating fetching the user name from local storage or API
  useEffect(() => {
    const storedUser = localStorage.getItem("userName") || "Guest";
    setUserName(storedUser);
  }, []);

  return (
    <div className="dashboard-container">
      {/* Header Section */}
      <div className="header">
        <img src="/logo.png" alt="EV Connect Logo" className="logo" />
        <h1>EV User</h1>
        <h3>Welcome, {userName}!</h3>
      </div>

      {/* Map Section */}
      <MapContainer
        center={[10.0, 76.0]}
        zoom={10}
        style={{ height: "400px", width: "100%" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      </MapContainer>

      {/* Search Button */}
      <button className="search-btn">
        <FaSearchLocation className="icon" /> Search Station Nearby
      </button>
    </div>
  );
};

export default EVUserDashboard;
