import React, { useEffect, useState } from "react";
import { FaSearchLocation, FaBars, FaTimes, FaSignOutAlt, FaList } from "react-icons/fa";
import { useNavigate } from "react-router-dom"; // Import navigation
import "../styles/EVUserDashboard.css"; // CSS for styling
import { useUser } from "../context/UserContext";

const EVUserDashboard = () => {
  const { user } = useUser();
  const [userName, setUserName] = useState("");
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate(); // React Router Navigation

  // Fetch user name from local storage or API
  useEffect(() => {
    const storedUser = localStorage.getItem("userName") || "Guest";
    setUserName(storedUser);
  }, []);

  // Function to navigate to the StationMap page
  const handleSearchClick = () => {
    navigate("/stationmap");
  };

  // Function to navigate to View Booking Page
  const handleViewBooking = () => {
    navigate("/viewbookingevuser");
  };

  // Function to handle Logout
  const handleLogout = () => {
    localStorage.removeItem("user"); // Clear user data
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar Toggle Area */}
      <div className="sidebar-trigger" onMouseEnter={() => setIsSidebarOpen(true)}></div>

      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`} onMouseLeave={() => setIsSidebarOpen(false)}>
        <button className="close-btn" onClick={() => setIsSidebarOpen(false)}>
          <FaTimes />
        </button>
        <h2>Menu</h2>
        <button className="sidebar-btn" onClick={handleViewBooking}>
          <FaList /> View Booking
        </button>
        <button className="sidebar-btn logout-btn" onClick={handleLogout}>
          <FaSignOutAlt /> Logout
        </button>
      </div>

      {/* Header Section */}
      <div className="header">
        {/* Logo */}
        <img src="../image/logo.png" alt="Logo" className="logo" />
        <h1>EV User</h1>
        <h3>Welcome, {user.name}</h3>
      </div>

      {/* Search Button */}
      <button className="search-btn" onClick={handleSearchClick}>
        <FaSearchLocation className="icon" /> Search Station Nearby
      </button>
    </div>
  );
};

export default EVUserDashboard;
