import React, { useEffect, useState } from "react";
import "../styles/ViewBkEVuser.css";
import { useUser } from "../context/UserContext";

const ViewBookingEVUser = () => {
  const { user } = useUser();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBookings();
  }, []);

  // Fetch bookings for the logged-in user
  const fetchBookings = async () => {
    try {
      const response = await fetch(`http://localhost:5006/api/bookings/user/${user.id}`);
      const data = await response.json();
      setBookings(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching bookings:", error);
      setLoading(false);
    }
  };

  return (
    <div className="view-booking-container">
      <h2>Your Bookings</h2>
      {loading ? (
        <p>Loading...</p>
      ) : bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        <table className="booking-table">
          <thead>
            <tr>
              <th>Station Name</th>
              <th>Slot Time</th>
              <th>Status</th>
              <th>Payment</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking._id}>
                <td>{booking.chargingStation.name}</td>
                <td>{booking.slot.from} - {booking.slot.to}</td>
                <td>{booking.status}</td>
                <td>{booking.paymentStatus}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ViewBookingEVUser;
