import React, { useEffect, useState } from "react";
import axios from "axios";

const UpdateSlot = () => {
  const [slots, setSlots] = useState([]);
  const [stations, setStations] = useState([]);

  useEffect(() => {
    fetchSlots();
  }, []);

  const fetchSlots = async () => {
    try {
      const response = await axios.get("/api/slots/operator-slots", { withCredentials: true });
      setStations(response.data.stations);
      setSlots(response.data.slots);
    } catch (error) {
      console.error("Error fetching slots:", error);
    }
  };

  const toggleSlotStatus = async (slotId, currentStatus) => {
    try {
      await axios.put(`/api/slots/update-slot/${slotId}`, {}, { withCredentials: true });
      setSlots(slots.map(slot =>
        slot._id === slotId ? { ...slot, operationalStatus: currentStatus === "available" ? "notavailable" : "available" } : slot
      ));
    } catch (error) {
      console.error("Error updating slot status:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-3">Update Slot Availability</h2>
      {stations.map(station => (
        <div key={station._id} className="card mb-3">
          <div className="card-header">
            <h4>{station.name}</h4>
          </div>
          <div className="card-body">
            {slots.filter(slot => slot.stationId._id === station._id).length > 0 ? (
              <ul className="list-group">
                {slots.filter(slot => slot.stationId._id === station._id).map(slot => (
                  <li key={slot._id} className="list-group-item d-flex justify-content-between align-items-center">
                    Slot: {slot.from} - {slot.to} | Status: 
                    <span className={`badge ${slot.operationalStatus === "available" ? "bg-success" : "bg-danger"} ms-2`}>
                      {slot.operationalStatus}
                    </span>
                    <button
                      className={`btn btn-sm ${slot.operationalStatus === "available" ? "btn-danger" : "btn-success"} ms-3`}
                      onClick={() => toggleSlotStatus(slot._id, slot.operationalStatus)}
                    >
                      {slot.operationalStatus === "available" ? "Make Not Available" : "Make Available"}
                    </button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No slots found for this station.</p>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default UpdateSlot;
