import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditSlots = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [slots, setSlots] = useState("");

  useEffect(() => {
    fetchStation();
  }, []);

  const fetchStation = async () => {
    try {
      const response = await fetch(`http://localhost:5006/api/stations/${id}`);
      const data = await response.json();
      setSlots(data.slots);
    } catch (error) {
      console.error("Error fetching station details:", error);
    }
  };

  const handleUpdateSlots = async () => {
    try {
      const response = await fetch(`http://localhost:5006/api/stations/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ slots }),
      });

      if (response.ok) {
        navigate("/manage-stations");
      }
    } catch (error) {
      console.error("Error updating slots:", error);
    }
  };

  return (
    <div style={styles.container}>
      <h2>Edit Slots</h2>
      <input
        type="number"
        value={slots}
        onChange={(e) => setSlots(e.target.value)}
        placeholder="Enter new slot count"
        style={styles.input}
      />
      <button onClick={handleUpdateSlots} style={styles.updateButton}>Update Slots</button>
    </div>
  );
};

const styles = {
  container: { textAlign: "center", padding: "20px" },
  input: { padding: "10px", fontSize: "16px", marginBottom: "10px" },
  updateButton: { backgroundColor: "#4CAF50", color: "white", padding: "10px", border: "none", cursor: "pointer" },
};

export default EditSlots;
