import React, { useEffect, useState } from "react";
import { FaSearchLocation, FaSignOutAlt, FaList } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import "../styles/EVUserDashboard.css";
import { useUser } from "../context/UserContext";
import { Link } from "react-router-dom";

const EVUserDashboard = () => {
  const { user } = useUser();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  // Function to navigate to the StationMap page
  const handleSearchClick = () => {
    navigate("/stationmap");
  };

  // Function to navigate to View Booking Page
  const handleViewBooking = () => {
    navigate("/viewbookingevuser");
  };

  // Function to handle Logout
  const handleLogout = () => {
    localStorage.removeItem("user"); // Clear user data
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar - Hidden by Default, Shows on Hover */}
      <nav className="sidebar">
        {/* Logo */}
        <img src="../image/logo.png" alt="Logo" className="logo" />   
        <ul>
          <li><Link to="/operator">Dashboard</Link></li>
          <li><Link to="/operator/manage-stations">Manage Stations</Link></li>
          <li><Link to="/operator/update-slots">Update Slot Availability</Link></li>
          <li><Link to="/operator/bookings">View Bookings</Link></li>
          <li><Link to="/operator/station-map">Station Map</Link></li>
          <li><Link to="/">Logout</Link></li>
        </ul>
      </nav>
      <div
        className={`sidebar ${isSidebarOpen ? "open" : ""}`}
        onMouseEnter={() => setIsSidebarOpen(true)}
        onMouseLeave={() => setIsSidebarOpen(false)}
      >
        <h2>Menu</h2>
        <button className="sidebar-btn" onClick={handleViewBooking}>
          <FaList /> View Booking
        </button>
        <button className="sidebar-btn logout-btn" onClick={handleLogout}>
          <FaSignOutAlt /> Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header className="headers">

          <img src="../image/logo.png" alt="Logo" className="logo" />
          <h1>EV User</h1>
          <h3>Welcome, {user.name}</h3>

        </header>

        {/* Search Button */}
        <button className="search-btn" onClick={handleSearchClick}>
          <FaSearchLocation className="icon" />
          Search Nearby Stations
        </button>
      </div>
    </div>
  );
};

export default EVUserDashboard;
