import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";

// 🔑 Your Google API Key
const GOOGLE_MAPS_API_KEY = "AIzaSyAS9zfp0wdczlh-d1WDk1VHAbsTrhsDg-E";

const ManageStations = () => {
  const [stations, setStations] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    latitude: "",
    longitude: "",
    slots: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetchStations();
  }, []);

  // 📌 Fetch stations from backend
  const fetchStations = async () => {
    try {
      const response = await fetch("http://localhost:5006/api/stations");
      const data = await response.json();
      setStations(data);
    } catch (error) {
      console.error("Error fetching stations:", error);
    }
  };

  // 📌 Handle form input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // 📌 Use Current Location for Latitude & Longitude
  const useCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const lat = position.coords.latitude;
          const lng = position.coords.longitude;
          setFormData({ ...formData, latitude: lat, longitude: lng });
        },
        (error) => {
          console.error("Error getting location:", error);
        }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  };

  // 📌 Handle map click to update latitude & longitude
  const handleMapClick = (event) => {
    const lat = event.latLng.lat();
    const lng = event.latLng.lng();
    setFormData({ ...formData, latitude: lat, longitude: lng });
  };

  // 📌 Add a new station
  const handleAddStation = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5006/api/stations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        fetchStations(); // Refresh list after adding
        setFormData({ name: "", latitude: "", longitude: "", slots: "" });
      }
    } catch (error) {
      console.error("Error adding station:", error);
    }
  };

  // 📌 Delete a station
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5006/api/stations/${id}`, { method: "DELETE" });
      fetchStations();
    } catch (error) {
      console.error("Error deleting station:", error);
    }
  };

  // 📌 Navigate to update page
  const handleUpdate = (id) => {
    navigate(`/update-station/${id}`);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Manage Charging Stations</h2>

      {/* Form for Adding Stations */}
      <form onSubmit={handleAddStation} style={styles.form}>
        <input type="text" name="name" placeholder="Station Name" value={formData.name} onChange={handleChange} required style={styles.input} />
        <input type="number" name="slots" placeholder="Total Slots" value={formData.slots} onChange={handleChange} required style={styles.input} />

        {/* Use Current Location Button */}
        <button type="button" onClick={useCurrentLocation} style={styles.locationButton}>
          📍 Use Current Location
        </button>

        {/* Map for Selecting Location */}
        <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
          <GoogleMap
            mapContainerStyle={styles.mapContainer}
            center={formData.latitude && formData.longitude ? { lat: formData.latitude, lng: formData.longitude } : { lat: 10.0, lng: 76.0 }}
            zoom={10}
            onClick={handleMapClick}
          >
            {formData.latitude && formData.longitude && (
              <Marker position={{ lat: formData.latitude, lng: formData.longitude }} />
            )}
          </GoogleMap>
        </LoadScript>

        <p style={styles.locationText}>Selected Location: {formData.latitude}, {formData.longitude}</p>

        <button type="submit" style={styles.addButton}>Add Station</button>
      </form>

      {/* Table to Display Stations */}
      <h2 style={styles.heading2}>STATION DETAILS</h2>

      <div style={styles.tableContainer}>

        <table style={styles.table}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Latitude</th>
              <th>Longitude</th>
              <th>Slots</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {stations.map((station) => (
              <tr key={station._id}>
                <td>{station.name}</td>
                <td>{station.latitude}</td>
                <td>{station.longitude}</td>
                <td>{station.slots}</td>
                <td>
                  <button onClick={() => handleUpdate(station._id)} style={styles.updateButton}>Update</button>
                  <button onClick={() => handleDelete(station._id)} style={styles.deleteButton}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// 🎨 Styling Object
const styles = {
  container: {
    padding: "20px",
    textAlign: "center",
    backgroundImage: "url('https://source.unsplash.com/1600x900/?technology,charging')",
    backgroundSize: "cover",
    minHeight: "100vh",
    color: "#fff",
  },
  heading: {
    fontSize: "28px",
    marginBottom: "20px",
    textShadow: "2px 2px 8px rgba(0,0,0,0.3)",
  },
  heading2: {
    fontSize: "24px",
    marginBottom: "20px",
    textShadow: "2px 2px 8px rgba(0,0,0,0.3)",
    color: "#f44336",
  },
  form: {
    marginBottom: "20px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  input: {
    width: "300px",
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "none",
    fontSize: "16px",
  },
  locationButton: {
    padding: "5px 10px",
    width: "30%",
    backgroundColor: "#ff9800",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginBottom: "10px",
  },
  mapContainer: {
    height: "400px",
    width: "50%",
    borderRadius: "10px",
    marginBottom: "10px",
  },
  locationText: {
    fontSize: "16px",
    fontWeight: "bold",
  },
  addButton: {
    padding: "10px 20px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    width: "30%",
  },
  tableContainer: {
    display: "flex",
    justifyContent: "center",
    marginTop: "20px",
  },
  table: {
    width: "80%",
    borderCollapse: "collapse",
    background: "#fff",
    color: "#000",
    borderRadius: "10px",
  },
  updateButton: {
    backgroundColor: "#008CBA",
    color: "#fff",
    padding: "5px 10px",
    border: "none",
    cursor: "pointer",
  },
  deleteButton: {
    backgroundColor: "#f44336",
    color: "#fff",
    padding: "5px 10px",
    border: "none",
    cursor: "pointer",
  },
};

export default ManageStations;
